const track=document.getElementById("image-track");window.onmousedown=e=>{track.dataset.mouseDownAt=e.clientX},window.onmousemove=e=>{if("0"===track.dataset.mouseDownAt)return;const percentage=(parseFloat(track.dataset.mouseDownAt)-e.clientX)/(window.innerWidth/2)*-100;track.style.transform=`translate(${percentage}%, -50%)`},alert("hi jirk");